import "core-js/modules/es6.promise";
import axios from 'axios';
import _ from 'lodash';
var API_BASE_URL = process.env.VUE_APP_MW_API_BASE_URL;
var DUMMY_TOKEN = process.env.VUE_APP_MW_DEV_ACCESS_TOKEN;
export var MW_API = axios.create({
  baseURL: API_BASE_URL,
  timeout: 600000
});
MW_API.interceptors.request.use(function (config) {
  var token = null;

  if (_.has(config, 'share_token')) {
    token = config.share_token;
  } else {
    token = localStorage.getItem('ps_access_token') || DUMMY_TOKEN;
  }

  if (token) {
    config.headers.Authorization = 'Bearer ' + token;
  }

  return config;
}, function (error) {
  return Promise.reject(error);
});
MW_API.interceptors.response.use(function (response) {
  return response;
}, function (error) {
  return Promise.reject(error);
});
