import defaultRoutes from './_routerConfig'
import _ from 'lodash'

/* eslint-disable */
import PFDashboard from '@/components/pages/dashboard/Dashboard'
import Step1Import from '@/components/pages/Step1Import'
import Step2Validate from '@/components/pages/Step2Validate'
import Step3Process from '@/components/pages/Step3Process'
import Step4Result from '@/components/pages/Step4Result'

const routes = {
  PFDashboard,
  Step1Import,
  Step2Validate,
  Step3Process,
  Step4Result
}
_.forEach(defaultRoutes.children, (brand, key) => {
  brand.component = routes[brand.name]
  if(brand.children) {
    _.forEach(brand.children, (brand2, key2) => {
      brand2.component = routes[brand2.name]
    })
  }
})

export default [defaultRoutes]
export const routerRender = defaultRoutes.children
