const DUMMY_CLIENT_ID = process.env.VUE_APP_MT_CLIENT_ID
const DUMMY_TOKEN = process.env.VUE_APP_MT_API_DEV_ACCESS_TOKEN
export default {
  getCurrentClientId () {
    return localStorage.getItem('ps_current_client_id') || DUMMY_CLIENT_ID
  },
  getCurrentAccessToken () {
    return localStorage.getItem('ps_access_token') || DUMMY_TOKEN
  },
  getItem: (name) => {
    return localStorage.getItem(name)
  }
}
