import axios from 'axios'
// const ROOT_API = process.env.VUE_APP_SDK_API_BASE_URL
const ROOT_API = process.env.VUE_APP_SDK_API_BASE_URL

const sdkAxios = axios.create({
  baseURL: ROOT_API,
  timeout: 600000
})

sdkAxios.interceptors.request.use(function (config) {
  return config
}, function (error) {
  return Promise.reject(error)
})

sdkAxios.interceptors.response.use(function (response) {
  return response
}, function (error) {
  return Promise.reject(error)
})

export default sdkAxios
